mapper.insert:
INSERT INTO
    cells (
        UNQ_ID,
        POST_DATE,
        CONTENT
    ) VALUES (
        #{unqId},
        #{postDate},
        #{content}
    )

mapper.update:
UPDATE cells
SET
    POST_DATE = #{postDate}
WHERE
    UNQ_ID = #{id}

UPDATE cells
SET
    POST_DATE = #{postDate},
    CONTENT = #{content}
WHERE
    UNQ_ID = #{unqId}
